select * from chat_logs;

CREATE TABLE chat_logs (
    chat_id SERIAL PRIMARY KEY,
    user_id INT NULL REFERENCES users(user_id) ON DELETE SET NULL,
    session_id VARCHAR(100),               -- mã phiên trò chuyện (nếu có)
    sender VARCHAR(20) NOT NULL,           -- 'user' hoặc 'bot'
    message TEXT NOT NULL,                 -- nội dung câu hỏi / trả lời
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);
TRUNCATE TABLE chat_logs RESTART IDENTITY CASCADE;

